// Get the input data using n8n's special syntax
const inputData = JSON.parse(JSON.stringify($input.all()[0].json));

// Process articles to remove unwanted fields
const cleanedArticles = inputData.articles.map(article => {
  const { publishedAt, urlToImage, url, author,content, ...rest } = article;
  return {
    ...rest,
    source: { name: article.source.name } // Keep only source name
  };
});

// Return the cleaned data in n8n format
return [{
  json: {
    status: inputData.status,
    totalResults: inputData.totalResults,
    articles: cleanedArticles
  }
}];

const allCandles = [];
let contentData = null;

for (const item of items) {
  if (item.json.allCandles !== undefined) {
    allCandles.push(...item.json.allCandles);
  }
  if (item.json.message && item.json.message.content !== undefined) {
    contentData = item.json.message.content;
  }
}

// Get 1D candles
const oneDayCandles = allCandles.find(c => c.timeframe === "1d")?.candles || [];

// --- Price Data Processing ---
const closePrices = [];
const highPrices = [];
const lowPrices = [];
const volumes = [];

for (const c of oneDayCandles) {
  const close = parseFloat(c[4]);
  const high = parseFloat(c[2]);
  const low = parseFloat(c[3]);
  const volume = parseFloat(c[5]);

  if (!isNaN(close)) closePrices.push(close);
  if (!isNaN(high)) highPrices.push(high);
  if (!isNaN(low)) lowPrices.push(low);
  if (!isNaN(volume)) volumes.push(volume);
}

// --- Latest Candle Data ---
const latest = oneDayCandles[oneDayCandles.length - 1] || [];
const price = parseFloat(latest[4]) || getAverage(closePrices);
const open = parseFloat(latest[1]) || getAverage(closePrices);
const highPrice = parseFloat(latest[2]) || getAverage(highPrices);
const lowPrice = parseFloat(latest[3]) || getAverage(lowPrices);
const volume = parseFloat(latest[5]) || getAverage(volumes);
const priceChangePercent = open && price ? (((price - open) / open) * 100).toFixed(2) : "N/A";

// --- Support/Resistance Levels ---
const supportLevels = price ? [ 
  (price * 0.95).toFixed(2), 
  (price * 0.90).toFixed(2) 
] : ["N/A", "N/A"];

const resistanceLevels = price ? [
  (price * 1.05).toFixed(2),
  (price * 1.10).toFixed(2)
] : ["N/A", "N/A"];

// --- Indicator Calculations with NaN handling ---

// RSI Calculation with fallback
function calculateRSI(prices, period = 14) {
  if (!prices || prices.length < period + 1) {
    // Fallback: Use price momentum as proxy
    if (prices && prices.length > 1) {
      const gains = prices.slice(-5).filter((v,i,a) => i > 0 && v > a[i-1]).length;
      const losses = prices.slice(-5).filter((v,i,a) => i > 0 && v < a[i-1]).length;
      return gains > losses ? 60 : losses > gains ? 40 : 50; // Neutral range
    }
    return 50; // Neutral default
  }

  let gains = 0, losses = 0;
  for (let i = 1; i <= period; i++) {
    const diff = prices[i] - prices[i - 1];
    if (diff >= 0) gains += diff;
    else losses -= diff;
  }

  let avgGain = gains / period;
  let avgLoss = losses / period;

  for (let i = period + 1; i < prices.length; i++) {
    const diff = prices[i] - prices[i - 1];
    if (diff >= 0) {
      avgGain = (avgGain * (period - 1) + diff) / period;
      avgLoss = (avgLoss * (period - 1)) / period;
    } else {
      avgGain = (avgGain * (period - 1)) / period;
      avgLoss = (avgLoss * (period - 1) - diff) / period;
    }
  }

  if (avgLoss === 0) return 100;
  const rs = avgGain / avgLoss;
  return +(100 - 100 / (1 + rs)).toFixed(2);
}

// MACD Calculation with fallback
function calculateMACD(prices, shortPeriod = 12, longPeriod = 26, signalPeriod = 9) {
  if (!prices || prices.length < longPeriod + signalPeriod) {
    // Fallback: Use simple price trend analysis
    if (prices && prices.length > 3) {
      const shortAvg = getAverage(prices.slice(-shortPeriod));
      const longAvg = getAverage(prices.slice(-longPeriod));
      const diff = shortAvg - longAvg;
      return {
        macd: +diff.toFixed(3),
        signal: +(diff * 0.9).toFixed(3), // Approximate signal line
        histogram: +(diff * 0.1).toFixed(3),
        state: diff > 0 ? "Bullish" : diff < 0 ? "Bearish" : "Neutral"
      };
    }
    return {
      macd: 0,
      signal: 0,
      histogram: 0,
      state: "Neutral"
    };
  }

  const ema = (data, period) => {
    const k = 2 / (period + 1);
    let emaVals = [data.slice(0, period).reduce((a, b) => a + b) / period];
    for (let i = period; i < data.length; i++) {
      emaVals.push(data[i] * k + emaVals[emaVals.length - 1] * (1 - k));
    }
    return emaVals;
  };

  const shortEma = ema(prices, shortPeriod);
  const longEma = ema(prices, longPeriod);
  const macdLine = shortEma.slice(longEma.length - shortEma.length).map((v, i) => v - longEma[i]);
  const signalLine = ema(macdLine, signalPeriod);

  const latestMacd = macdLine[macdLine.length - 1];
  const latestSignal = signalLine[signalLine.length - 1];
  const histogram = +(latestMacd - latestSignal).toFixed(3);
  const state = histogram > 0 ? "Bullish" : histogram < 0 ? "Bearish" : "Neutral";

  return { 
    macd: +latestMacd.toFixed(3), 
    signal: +latestSignal.toFixed(3), 
    histogram, 
    state 
  };
}

// Stochastic RSI with fallback
function calculateStochasticRSI(prices, rsiPeriod = 14, stochPeriod = 14) {
  const rsiVals = [];
  for (let i = rsiPeriod; i <= prices.length; i++) {
    const rsi = calculateRSI(prices.slice(i - rsiPeriod, i), rsiPeriod);
    if (rsi !== null) rsiVals.push(rsi);
  }

  if (rsiVals.length < stochPeriod) {
    // Fallback: Use simple RSI position
    if (rsiVals.length > 0) {
      const lastRSI = rsiVals[rsiVals.length - 1];
      return lastRSI > 70 ? 80 : lastRSI < 30 ? 20 : 50;
    }
    return 50; // Neutral default
  }

  const recentRSI = rsiVals.slice(-stochPeriod);
  const min = Math.min(...recentRSI);
  const max = Math.max(...recentRSI);
  const last = recentRSI[recentRSI.length - 1];

  return +(((last - min) / (max - min)) * 100).toFixed(2);
}

// ATR Calculation with fallback
function calculateATR(highs, lows, closes, period = 14) {
  if (!highs || !lows || !closes || highs.length < period + 1) {
    // Fallback: Use simple percentage of price
    if (highs && highs.length > 0 && lows && lows.length > 0) {
      const avgRange = (getAverage(highs) - getAverage(lows)) / getAverage(closes);
      return +(avgRange * price * 0.7).toFixed(2); // 70% of average range
    }
    return price ? +(price * 0.02).toFixed(2) : 0; // 2% of price as default
  }

  const tr = [];
  for (let i = 1; i < highs.length; i++) {
    tr.push(Math.max(
      highs[i] - lows[i],
      Math.abs(highs[i] - closes[i - 1]),
      Math.abs(lows[i] - closes[i - 1])
    ));
  }

  const atr = tr.slice(-period).reduce((a, b) => a + b) / period;
  return +atr.toFixed(2);
}

// --- Helper function for averages ---
function getAverage(arr) {
  if (!arr || arr.length === 0) return 0;
  const sum = arr.reduce((a, b) => a + b, 0);
  return sum / arr.length;
}

// --- Calculate All Indicators ---
const rsi = calculateRSI(closePrices);
const macdResult = calculateMACD(closePrices);
const stochasticRsi = calculateStochasticRSI(closePrices);
const atr = calculateATR(highPrices, lowPrices, closePrices);

// --- Risk/Reward Calculations ---
const stopLoss = price && atr ? (price - atr * 2).toFixed(2) : price ? (price * 0.98).toFixed(2) : "N/A";
const takeProfits = price && atr ? [
  (price + atr * 1.5).toFixed(2),
  (price + atr * 2.5).toFixed(2),
  (price + atr * 3.5).toFixed(2)
] : price ? [
  (price * 1.03).toFixed(2),
  (price * 1.06).toFixed(2),
  (price * 1.09).toFixed(2)
] : ["N/A", "N/A", "N/A"];

// --- Bollinger Bands with fallback ---
const bollingerBands = price ? {
  upper: (price * 1.05).toFixed(2),
  lower: (price * 0.95).toFixed(2),
  middle: price.toFixed(2)
} : { 
  upper: "N/A", 
  lower: "N/A", 
  middle: "N/A" 
};

// --- Sentiment Analysis ---
const relevantNews = contentData?.articles?.slice(0, 3) || [];
const hasPositiveNews = relevantNews.some(article => 
  article.title?.toLowerCase().includes('bullish') || 
  article.description?.toLowerCase().includes('growth')
);

const longTermSentiment = hasPositiveNews ? "Bullish" : "Neutral";
const shortTermSentiment = stochasticRsi > 80 ? "Overbought" : stochasticRsi < 20 ? "Oversold" : "Neutral";
const sentimentSummary = `${
  longTermSentiment === "Bullish" ? 
  "Positive long-term outlook" : 
  "Neutral long-term outlook"
} with ${
  shortTermSentiment.toLowerCase()
} short-term conditions`;

// --- Final Output ---
return [{
  json: {
    // Raw Data
    allCandles,
    content: contentData,

    // Price Data
    price: price || "N/A",
    priceChangePercent,
    highPrice: highPrice || "N/A",
    lowPrice: lowPrice || "N/A",
    volume: volume || "N/A",

    // Technical Indicators
    rsi: rsi || "N/A",
    macd: macdResult || {macd: "N/A", signal: "N/A", histogram: "N/A", state: "N/A"},
    stochasticRsi: stochasticRsi || "N/A",
    atr: atr || "N/A",
    bollingerBands,

    // Levels
    supportLevels,
    resistanceLevels,

    // Risk Management
    stopLoss,
    takeProfits,

    // Sentiment Analysis
    longTermSentiment,
    shortTermSentiment,
    sentimentSummary,
    relevantNews,

    // Derived Insights
    trendSummary: getTrendSummary(rsi, macdResult.state),
    momentumScore: calculateMomentumScore(closePrices),
    momentumDirection: macdResult.histogram > 0 ? "Upward" : macdResult.histogram < 0 ? "Downward" : "Neutral",
    volumeTrend: getVolumeTrend(volumes),
    volatility7d: calculateVolatility(closePrices, 7),
    priceActionInsight: getPriceActionInsight(price, supportLevels, resistanceLevels)
  }
}];

// --- Enhanced Helper Functions ---
function getTrendSummary(rsi, macdState) {
  if (rsi === null || !macdState) {
    // Fallback trend detection using simple moving averages
    if (closePrices && closePrices.length > 10) {
      const shortAvg = getAverage(closePrices.slice(-5));
      const longAvg = getAverage(closePrices.slice(-10));
      return shortAvg > longAvg ? "Mild uptrend" : shortAvg < longAvg ? "Mild downtrend" : "Neutral trend";
    }
    return "Insufficient data";
  }

  if (rsi > 70 && macdState === "Bearish") return "Strong downtrend";
  if (rsi < 30 && macdState === "Bullish") return "Strong uptrend";
  if (macdState === "Bullish") return "Mild uptrend";
  if (macdState === "Bearish") return "Mild downtrend";
  return "Neutral trend";
}

function calculateMomentumScore(prices) {
  if (!prices || prices.length < 5) {
    // Fallback: Use price change percentage
    if (prices && prices.length > 1) {
      const change = ((prices[prices.length-1] - prices[0]) / prices[0]) * 100;
      return +(Math.min(Math.abs(change), 10)).toFixed(1); // Cap at 10
    }
    return 5; // Neutral default
  }
  const recent = prices.slice(-5);
  const slope = (recent[4] - recent[0]) / 4;
  return +(Math.abs(slope) * 10).toFixed(1); // Score 0-10
}

function getVolumeTrend(volumes) {
  if (!volumes || volumes.length < 3) {
    // Fallback: Check if last volume is above average
    if (volumes && volumes.length > 0) {
      const avg = getAverage(volumes);
      return volumes[volumes.length-1] > avg ? "Increasing" : "Decreasing";
    }
    return "Stable";
  }
  const avg = arr => arr.reduce((a,b) => a + b, 0) / arr.length;
  const recent = volumes.slice(-3);
  const prev = volumes.slice(-6, -3);
  return avg(recent) > avg(prev) ? "Increasing" : "Decreasing";
}

function calculateVolatility(prices, days) {
  if (!prices || prices.length < days) {
    // Fallback: Use ATR percentage if available
    if (atr && price) return +(atr / price * 100).toFixed(1) + "%";
    return "3.0%"; // Default average volatility
  }
  const recent = prices.slice(-days);
  const changes = [];
  for (let i = 1; i < recent.length; i++) {
    changes.push(Math.abs((recent[i] - recent[i-1]) / recent[i-1]));
  }
  return (changes.reduce((a,b) => a + b, 0) / changes.length * 100).toFixed(1) + "%";
}

function getPriceActionInsight(price, supportLevels, resistanceLevels) {
  if (!price) return "No price data available";

  const nearSupport = supportLevels.some(level => 
    level !== "N/A" && Math.abs(price - parseFloat(level)) < price * 0.01
  );
  const nearResistance = resistanceLevels.some(level => 
    level !== "N/A" && Math.abs(price - parseFloat(level)) < price * 0.01
  );

  if (nearSupport) return "Price near support level - potential bounce";
  if (nearResistance) return "Price near resistance level - potential rejection";

  // Fallback: Use RSI position if available
  if (rsi) {
    return rsi > 70 ? "Price may be overbought" : 
           rsi < 30 ? "Price may be oversold" : 
           "Price in middle of range - wait for breakout";
  }
  return "Price in middle of range - wait for breakout";
}

