const inputText = $json["output"] || "";
if (typeof inputText !== "string") {
  throw new Error("AI output must be a string.");
}

// Clean up triple backticks and trim
const cleaned = inputText.replace(/```(?:html|text)?/gi, '').replace(/```/g, '').trim();

// Desired max length for each message (Telegram limit is 4096)
const MAX_LENGTH = 4000;

// If short enough, return as one message
if (cleaned.length <= MAX_LENGTH) {
  return [{ json: { blockNumber: 1, content: cleaned } }];
}

// Smart split: look for closest sentence boundary near middle
const mid = Math.floor(cleaned.length / 2);
const splitAt = cleaned.lastIndexOf(".", mid) !== -1
  ? cleaned.lastIndexOf(".", mid) + 1
  : cleaned.lastIndexOf("\n", mid) !== -1
    ? cleaned.lastIndexOf("\n", mid) + 1
    : mid; // fallback: hard split

const firstHalf = cleaned.substring(0, splitAt).trim();
const secondHalf = cleaned.substring(splitAt).trim();

return [
  { json: { blockNumber: 1, content: firstHalf } },
  { json: { blockNumber: 2, content: secondHalf } }
];
